package me.melo.cloud.usercenter.client.feign;import me.melo.cloud.common.result.Result;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestParam;@FeignClient(name = "melo-usercenter", contextId = "SysUserFeign")public interface ISysUserFeign {    @GetMapping("/feign/user/query/by/id")    Result<Object> queryById(@RequestParam("id") Long id);    @GetMapping("/feign/user/query/by/phone")    Result<Object> queryUserByPhone(@RequestParam("phoneNumber") String phoneNumber);    @GetMapping("/feign/user/query/by/account")    Result<Object> queryUserByAccount(@RequestParam("account") String account);    @GetMapping("/feign/user/query/by/openid")    Result<Object> queryUserByOpenId(@RequestParam("openid") String openid);}