package me.melo.cloud.usercenter.entity;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.annotation.TableName;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;import java.io.Serializable;import java.time.LocalDateTime;import java.util.List;/** * <p> * 权限表 * </p> * * @author ljhua * @since 2022-08-08 */@Data@TableName("sys_resource")@ApiModel(value = "SysResource对象", description = "权限表")public class SysResource implements Serializable {    private static final long serialVersionUID = 1L;    @ApiModelProperty("主键")    @TableId(value = "id", type = IdType.AUTO)    private Long id;    @ApiModelProperty("租户id")    private Long tenantId;    @ApiModelProperty("父id")    private Long parentId;    @ApiModelProperty("所有上级资源id的集合，便于查找")    private String ancestors;    @ApiModelProperty("资源名称")    private String resourceName;    @ApiModelProperty("资源标识")    private String resourceKey;    @ApiModelProperty("资源类型 1、模块 2、菜单 3、按钮 4、链接")    private String resourceType;    @ApiModelProperty("资源图标")    private String resourceIcon;    @ApiModelProperty("资源路径")    private String resourcePath;    @ApiModelProperty("资料链接")    private String resourceUrl;    @ApiModelProperty("资源级别")    private Integer resourceLevel;    @ApiModelProperty("是否显示")    private Boolean resourceShow;    @ApiModelProperty("是否缓存")    private Boolean resourceCache;    @ApiModelProperty("资源页面名称")    private String resourcePageName;    @ApiModelProperty("1有效，0禁用")    private Integer resourceStatus;    @ApiModelProperty("备注")    private String comments;    @ApiModelProperty("创建者")    private Long createdBy;    @ApiModelProperty("创建时间")    private LocalDateTime createdTime;    @ApiModelProperty("更新者")    private Long modifiedBy;    @ApiModelProperty("更新时间")    private LocalDateTime modifiedTime;    @ApiModelProperty("1:删除 0:不删除")    private Integer delFlag;    /**     * 是否是叶子节点(查询时，如果此值为 1，则表示只查询子节点)     */    @TableField(exist = false)    private Integer isLeaf;    /**     * 子菜单，必须初始化否则vue新增不展示树子菜单     */    @TableField(exist = false)    private List<SysResource> children;    /**     * 拥有资源权限角色集合     */    @TableField(exist = false)    private List<SysRole> roles;    /**     * 拥有资源权限角色id集合     */    @TableField(exist = false)    private List<Long> roleIds;    /**     * 拥有资源权限角色key集合     */    @TableField(exist = false)    private List<String> roleKeys;}