package me.melo.cloud.usercenter.entity;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.annotation.TableName;import java.io.Serializable;import java.time.LocalDateTime;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;/** * <p> * 角色表 * </p> * * @author ljhua * @since 2022-08-08 */@TableName("sys_role")@ApiModel(value = "SysRole对象", description = "角色表")public class SysRole implements Serializable {    private static final long serialVersionUID = 1L;    @ApiModelProperty("主键")    @TableId(value = "id", type = IdType.AUTO)    private Long id;    @ApiModelProperty("租户id")    private Long tenantId;    @ApiModelProperty("父id")    private Long parentId;    @ApiModelProperty("角色名称")    private String roleName;    @ApiModelProperty("角色标识")    private String roleKey;    @ApiModelProperty("角色级别")    private Integer roleLevel;    @ApiModelProperty("1有效，0禁用")    private Integer roleStatus;    @ApiModelProperty("角色数据权限类型,默认只能查询本人数据")    private String dataPermissionType;    @ApiModelProperty("描述")    private String comments;    @ApiModelProperty("创建者")    private Long createdBy;    @ApiModelProperty("创建时间")    private LocalDateTime createdTime;    @ApiModelProperty("更新者")    private Long modifiedBy;    @ApiModelProperty("更新时间")    private LocalDateTime modifiedTime;    @ApiModelProperty("1:删除 0:不删除")    private Integer delFlag;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public Long getTenantId() {        return tenantId;    }    public void setTenantId(Long tenantId) {        this.tenantId = tenantId;    }    public Long getParentId() {        return parentId;    }    public void setParentId(Long parentId) {        this.parentId = parentId;    }    public String getRoleName() {        return roleName;    }    public void setRoleName(String roleName) {        this.roleName = roleName;    }    public String getRoleKey() {        return roleKey;    }    public void setRoleKey(String roleKey) {        this.roleKey = roleKey;    }    public Integer getRoleLevel() {        return roleLevel;    }    public void setRoleLevel(Integer roleLevel) {        this.roleLevel = roleLevel;    }    public Integer getRoleStatus() {        return roleStatus;    }    public void setRoleStatus(Integer roleStatus) {        this.roleStatus = roleStatus;    }    public String getDataPermissionType() {        return dataPermissionType;    }    public void setDataPermissionType(String dataPermissionType) {        this.dataPermissionType = dataPermissionType;    }    public String getComments() {        return comments;    }    public void setComments(String comments) {        this.comments = comments;    }    public Long getCreatedBy() {        return createdBy;    }    public void setCreatedBy(Long createdBy) {        this.createdBy = createdBy;    }    public LocalDateTime getCreatedTime() {        return createdTime;    }    public void setCreatedTime(LocalDateTime createdTime) {        this.createdTime = createdTime;    }    public Long getModifiedBy() {        return modifiedBy;    }    public void setModifiedBy(Long modifiedBy) {        this.modifiedBy = modifiedBy;    }    public LocalDateTime getModifiedTime() {        return modifiedTime;    }    public void setModifiedTime(LocalDateTime modifiedTime) {        this.modifiedTime = modifiedTime;    }    public Integer getDelFlag() {        return delFlag;    }    public void setDelFlag(Integer delFlag) {        this.delFlag = delFlag;    }    @Override    public String toString() {        return "SysRole{" +            "id=" + id +            ", tenantId=" + tenantId +            ", parentId=" + parentId +            ", roleName=" + roleName +            ", roleKey=" + roleKey +            ", roleLevel=" + roleLevel +            ", roleStatus=" + roleStatus +            ", dataPermissionType=" + dataPermissionType +            ", comments=" + comments +            ", createdBy=" + createdBy +            ", createdTime=" + createdTime +            ", modifiedBy=" + modifiedBy +            ", modifiedTime=" + modifiedTime +            ", delFlag=" + delFlag +        "}";    }}